QUERY = "query getObjktDetailed($tokenId: String!, $fa2: String!) {\n  token(where: {token_id: {_eq: $tokenId}, fa_contract: {_eq: $fa2}}) {\n    ...TokenDefault\n    ...TokenHolders\n    english_auctions(where: {status: {_eq: \"active\"}}) {\n      ...EnglishAuctionDefault\n      __typename\n    }\n    dutch_auctions(where: {status: {_eq: \"active\"}}) {\n      ...DutchAuctionDefault\n      __typename\n    }\n    offers(order_by: {price: desc}, where: {status: {_eq: \"active\"}}) {\n      ...OfferDefault\n      __typename\n    }\n    tags {\n      tag {\n        name\n        __typename\n      }\n      __typename\n    }\n    asks(\n      order_by: {price: asc}\n      where: {price: {_gt: 0}, _or: [{status: {_eq: \"active\"}, seller: {owner_operators: {token: {fa_contract: {_eq: $fa2}, token_id: {_eq: $tokenId}}, allowed: {_eq: true}}, held_tokens: {quantity: {_gt: \"0\"}, token: {fa_contract: {_eq: $fa2}, token_id: {_eq: $tokenId}}}}}, {contract_version: {_lt: 4}, status: {_eq: \"active\"}}]}\n    ) {\n      ...AskDefault\n      __typename\n    }\n    hen_swaps(order_by: {price: asc}, where: {status: {_eq: \"active\"}}) {\n      ...SwapDefault\n      __typename\n    }\n    __typename\n  }\n}\n\nfragment TokenDefault on token {\n  pk\n  token_id\n  artifact_uri\n  description\n  display_uri\n  thumbnail_uri\n  fa_contract\n  rights\n  royalties {\n    ...Royalties\n    __typename\n  }\n  supply\n  timestamp\n  name\n  mime\n  last_listed\n  highest_offer\n  lowest_ask\n  flag\n  fa {\n    ...Fa\n    __typename\n  }\n  creators {\n    ...CreatorDefault\n    __typename\n  }\n  attributes {\n    attribute {\n      id\n      name\n      type\n      value\n      __typename\n    }\n    __typename\n  }\n  __typename\n}\n\nfragment Royalties on royalties {\n  id\n  amount\n  decimals\n  receiver_address\n  __typename\n}\n\nfragment Fa on fa {\n  active_auctions\n  active_listing\n  contract\n  description\n  name\n  owners\n  logo\n  volume_24h\n  volume_total\n  website\n  twitter\n  items\n  floor_price\n  type\n  collection_type\n  creator_address\n  collection_id\n  path\n  token_link\n  short_name\n  live\n  editions\n  collaborators {\n    ...Invitation\n    __typename\n  }\n  creator {\n    ...UserDefault\n    __typename\n  }\n  __typename\n}\n\nfragment Invitation on invitation {\n  collaborator_address\n  fa_contract\n  id\n  status\n  timestamp\n  update_timestamp\n  fa {\n    ...FaLight\n    __typename\n  }\n  holder {\n    ...UserDefault\n    __typename\n  }\n  __typename\n}\n\nfragment FaLight on fa {\n  contract\n  description\n  name\n  owners\n  logo\n  volume_24h\n  volume_total\n  floor_price\n  type\n  collection_type\n  collection_id\n  path\n  token_link\n  short_name\n  live\n  __typename\n}\n\nfragment UserDefault on holder {\n  address\n  alias\n  website\n  twitter\n  description\n  tzdomain\n  flag\n  logo\n  __typename\n}\n\nfragment CreatorDefault on token_creator {\n  creator_address\n  holder {\n    ...UserDefault\n    __typename\n  }\n  __typename\n}\n\nfragment TokenHolders on token {\n  holders(where: {quantity: {_gt: \"0\"}}) {\n    ...TokenHolderDefault\n    __typename\n  }\n  __typename\n}\n\nfragment TokenHolderDefault on token_holder {\n  holder_address\n  quantity\n  token_pk\n  holder {\n    ...UserDefault\n    __typename\n  }\n  __typename\n}\n\nfragment EnglishAuctionDefault on obj_english_auction {\n  id\n  hash\n  fa_contract\n  price_increment\n  reserve\n  shares\n  start_time\n  status\n  end_time\n  timestamp\n  token_pk\n  update_level\n  update_timestamp\n  hash\n  contract_version\n  seller_address\n  highest_bid\n  extension_time\n  highest_bidder_address\n  currency {\n    ...CurrencyDefault\n    __typename\n  }\n  bids {\n    ...EnglishAuctionBidsDefault\n    __typename\n  }\n  token {\n    ...TokenDefault\n    __typename\n  }\n  __typename\n}\n\nfragment CurrencyDefault on currency {\n  fa_contract\n  id\n  type\n  __typename\n}\n\nfragment EnglishAuctionBidsDefault on obj_english_bid {\n  amount\n  bidder {\n    ...UserDefault\n    __typename\n  }\n  bidder_address\n  id\n  timestamp\n  __typename\n}\n\nfragment DutchAuctionDefault on obj_dutch_auction {\n  id\n  hash\n  amount\n  amount_left\n  fa_contract\n  end_price\n  start_price\n  end_price\n  end_time\n  shares\n  start_time\n  status\n  timestamp\n  token_pk\n  update_level\n  update_timestamp\n  contract_version\n  hash\n  seller_address\n  seller {\n    ...UserDefault\n    __typename\n  }\n  __typename\n}\n\nfragment OfferDefault on obj_offer {\n  id\n  price\n  shares\n  status\n  timestamp\n  token_pk\n  update_timestamp\n  fa_contract\n  contract_version\n  currency {\n    ...CurrencyDefault\n    __typename\n  }\n  token {\n    ...TokenLight\n    __typename\n  }\n  buyer {\n    ...UserDefault\n    __typename\n  }\n  seller {\n    ...UserDefault\n    __typename\n  }\n  __typename\n}\n\nfragment TokenLight on token {\n  pk\n  token_id\n  artifact_uri\n  description\n  display_uri\n  thumbnail_uri\n  fa_contract\n  supply\n  timestamp\n  name\n  mime\n  flag\n  last_listed\n  creators {\n    ...CreatorDefault\n    __typename\n  }\n  fa {\n    ...Fa\n    __typename\n  }\n  __typename\n}\n\nfragment AskDefault on obj_ask {\n  id\n  amount\n  amount_left\n  price\n  shares\n  status\n  timestamp\n  token_pk\n  update_timestamp\n  fa_contract\n  contract_version\n  currency {\n    ...CurrencyDefault\n    __typename\n  }\n  seller {\n    ...UserDefault\n    __typename\n  }\n  __typename\n}\n\nfragment SwapDefault on hen_swap {\n  id\n  amount\n  amount_left\n  price\n  royalties\n  status\n  timestamp\n  token_pk\n  seller {\n    ...UserDefault\n    __typename\n  }\n  __typename\n}\n"
